def calculateTime(regexList){
	regexList.each{
		sTime =     System.currentTimeMillis()
		match = sourceStr =~ it
		eTime =     System.currentTimeMillis()
		println it+":\t:Time :"+(eTime-sTime)+" msec"
	}
}

def calculateUniqieWords(){
	Map map = [:] 
	match = sourceStr =~ /\b(\w+)\b/
	while(match.find()){
		val = match.group(1);
		if(map.containsKey(val)){
			map[val] += 1;
		}else{
			map.put(val, 1);
		}
	}
	map.sort().each {
		println it
	}
}

